renderitem.script:
effect = {matrix = 0, left = 0, top = 0, right = 0, bottom = 0, alpha = 1}
function setMatrixRECT(matrix, left, top, right, bottom, alpha)
effect.matrix = matrix effect.left = left effect.top = top effect.right = right effect.bottom = bottom effect.alpha = alpha end
function main() src_tex_id = kmGetScreenTexture(effect.matrix, effect.left, effect.top, effect.right, effect.bottom, 0.05) w = kmGetTexWidth(src_tex_id) * 1280 / kmGetOutputWidth() h = kmGetTexHeight(src_tex_id) * 720 / kmGetOutputHeight() first_pass_tex = kmSetRenderToTexture(w / range.f_block_size, h) mvp_matrix = kmNewMatrix() kmLoadIdentity(mvp_matrix) BHFx.setMvpMatrix(mvp_matrix) BHFx.setTextureSize(w / range.f_block_size, h) BHFx.setSTexture0(src_tex_id) BHFx.drawRect(2, 2)
kmSetRenderToDefault() res_tex = kmSetRenderToTexture(w / range.f_block_size, h / range.f_block_size) BVFx.setMvpMatrix(mvp_matrix) BVFx.setTextureSize(w / range.f_block_size, h / range.f_block_size) BVFx.setSTexture0(first_pass_tex) BVFx.drawRect(2, 2)
kmSetRenderToDefault() kmSetRenderDest(1, 0) kmClear(GL_STENCIL_BUFFER_BIT) kmDrawSolid(effect.matrix, effect.left, effect.top, effect.right, effect.bottom) kmSetRenderDest(0, 1) kmDrawRegionTexBox(res_tex, effect.matrix, effect.left, effect.top, effect.right, effect.bottom, 0.05, effect.alpha) kmReleaseTexture(src_tex_id) kmSetRenderDest(0, 0) end
