renderitem.script:
effect = {matrix = 0, left = 0, top = 0, right = 0, bottom = 0, alpha = 1}
function setMatrixRECT(matrix, left, top, right, bottom, alpha)
effect.matrix = matrix effect.left = left effect.top = top effect.right = right effect.bottom = bottom effect.alpha = alpha end
function main() src_tex_id = kmGetScreenTexture(effect.matrix, effect.left, effect.top, effect.right, effect.bottom)
w = kmGetTexWidth(src_tex_id) * 1280 / kmGetOutputWidth() h = kmGetTexHeight(src_tex_id) * 720 / kmGetOutputHeight() res_tex = kmSetRenderToTexture(w / (range.f_block_size * 2), h / (range.f_block_size * 2)) mvp_matrix = kmNewMatrix() kmLoadIdentity(mvp_matrix) kmDrawTexBox(src_tex_id, mvp_matrix, -1, 1, 1, -1) kmSetRenderToDefault() kmSetRenderDest(1, 0) kmClear(GL_STENCIL_BUFFER_BIT) kmDrawSolid(effect.matrix, effect.left, effect.top, effect.right, effect.bottom) kmSetRenderDest(0, 1) kmDrawRegionTexBox(res_tex, effect.matrix, effect.left, effect.top, effect.right, effect.bottom, 0, effect.alpha, 1) kmSetRenderDest(0, 0)
kmReleaseTexture(src_tex_id) end
